$(function () {
    scaleW=window.innerWidth/320;
    scaleH=window.innerHeight/480;
    var resizes = document.querySelectorAll('.resize');
    for (var j=0; j<resizes.length; j++) {
        resizes[j].style.width=parseInt(resizes[j].style.width)*scaleW+'px';
        resizes[j].style.height=parseInt(resizes[j].style.height)*scaleH+'px';
        resizes[j].style.top=parseInt(resizes[j].style.top)*scaleH+'px';
        resizes[j].style.left=parseInt(resizes[j].style.left)*scaleW+'px';
    }
    var a = new Swiper(".swiper-container", {
        direction: "vertical",
        nextButton: ".arrow-down",
        mousewheelControl: true,
        onInit: function(swiper){
            swiperAnimateCache(swiper);
            swiperAnimate(swiper);
        },
        onSlideChangeEnd: function(swiper){
            swiperAnimate(swiper);
        },
        onTransitionEnd: function(swiper){
            swiperAnimate(swiper);
        },
        watchSlidesProgress: true,

        onProgress: function(swiper){
            for (var i = 0; i < swiper.slides.length; i++){
                var slide = swiper.slides[i];
                var progress = slide.progress;
                var translate = progress*swiper.height/4;
                scale = 1 - Math.min(Math.abs(progress * 0.5), 1);
                var opacity = 1 - Math.min(Math.abs(progress/2),0.5);
                slide.style.opacity = opacity;
                es = slide.style;
                es.webkitTransform = es.MsTransform = es.msTransform = es.MozTransform = es.OTransform = es.transform = 'translate3d(0,'+translate+'px,-'+translate+'px) scaleY(' + scale + ')';

            }
        },

        onSetTransition: function(swiper, speed) {
            for (var i = 0; i < swiper.slides.length; i++){
                es = swiper.slides[i].style;
                es.webkitTransitionDuration = es.MsTransitionDuration = es.msTransitionDuration = es.MozTransitionDuration = es.OTransitionDuration = es.transitionDuration = speed + 'ms';

            }
        },

    });
    a.on("slideChangeStart", function () {
        var b;
        switch (a.activeIndex) {
            case 0:
            case 1:
                b = 1;
                break;
            case 2:
            case 3:
            case 4:
                b = 2;
                break;
            case 9:
                b = 10;
                break;
            default:
                b = 3
        }
        0 == a.activeIndex && $(".m-topbar").slideDown("slow"), $(".m-nav-btn").find("li[channel ='" + b + "']").addClass("active").siblings().removeClass("active"), 3 == b ? $("#nextPage").hide() : $("#nextPage").show()
    }), a.on("SlideChangeEnd", function () {
        0 != a.activeIndex && $(".m-topbar").slideUp("slow")
    }), $(".m-nav-btn").find("li").bind("click", function (b) {
        switch (b.preventDefault(), $(this).index()) {
            case 0:
                a.slideTo(0, 500);
                break;
            case 1:
                a.slideTo(2, 500);
                break;
            default:
                a.slideTo(6, 500)
        }
    }),

     $.fn.digits = function () {
        return this.each(function () {
            $(this).text($(this).text().replace(/(\d)(?=(\d\d\d)+(?!\d))/g, "$1,"))
        })
    }
});